import React, { useState } from 'react';
import './App.css';
import Layout from './components/Layout';
import SourceSelector from './components/SourceSelector';
import PdfViewer from './components/PdfViewer';
import QuizGenerator from './components/QuizGenerator';
import ProgressDashboard from './components/ProgressDashboard';
import ChatUI from './components/ChatUI';
import YouTubeRecommender from './components/YouTubeRecommender';


function App() {
  const [selectedPdfId, setSelectedPdfId] = useState('');
  const [userId] = useState('user_' + Date.now());
  const [activeTab, setActiveTab] = useState('quiz');
  const [videoCache, setVideoCache] = useState({});
  const [pdfs, setPdfs] = useState([]);

  // For delete modal
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [pdfToDelete, setPdfToDelete] = useState(null);

  // Handler to update cached videos for a PDF
  const handleCacheVideos = (pdfId, videos) => {
    setVideoCache((prev) => ({ ...prev, [pdfId]: videos }));
  };
  const handleRefreshVideos = (pdfId) => {
    setVideoCache((prev) => {
      const updated = { ...prev };
      delete updated[pdfId];
      return updated;
    });
  };

  // Handler to update PDFs list from SourceSelector
  const handlePdfsUpdate = (list) => setPdfs(list);

  // Handler for dropdown PDF select
  const handleDropdownSelect = (e) => setSelectedPdfId(e.target.value);

  // Handler for delete button (open modal)
  const handleDeleteClick = (pdf) => {
    setPdfToDelete(pdf);
    setShowDeleteModal(true);
  };

  // Handler for confirming delete
  const handleConfirmDelete = async () => {
    if (!pdfToDelete) return;
    try {
      await fetch(`http://localhost:5001/api/pdfs/${pdfToDelete._id}`, { method: 'DELETE' });
      setShowDeleteModal(false);
      setPdfToDelete(null);
      setSelectedPdfId('');
      // Refresh PDFs list
      document.dispatchEvent(new CustomEvent('refreshPdfs'));
    } catch (err) {
      alert('Failed to delete PDF');
    }
  };

  return (
    <Layout>
      <div className="app-container" style={{ minHeight: '100vh', background: '#f7f8fa' }}>
        <header className="app-header" style={{ marginBottom: '1.5rem' }}>
          <h1>📚 BeyondChats</h1>
          <p>Learn smarter with AI-powered learning</p>
        </header>

        {/* PDF Dropdown and Delete Button */}
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: '1.5rem', gap: '1rem' }}>
          <SourceSelector 
            onSelectPdf={setSelectedPdfId}
            selectedPdfId={selectedPdfId}
            onPdfsUpdate={handlePdfsUpdate}
            hideList
          />
          <select value={selectedPdfId} onChange={handleDropdownSelect} style={{ padding: '0.75rem', borderRadius: '8px', border: '1px solid #ccc', minWidth: 220 }}>
            <option value="">Select PDF</option>
            {pdfs.map(pdf => (
              <option key={pdf._id} value={pdf._id}>{pdf.originalname} ({pdf.pages} pages)</option>
            ))}
          </select>
          {selectedPdfId && pdfs.length > 0 && (
            <button onClick={() => handleDeleteClick(pdfs.find(p => p._id === selectedPdfId))} style={{ background: '#ff4d4f', color: 'white', border: 'none', borderRadius: '6px', padding: '0.5rem 1.2rem', fontWeight: 600, cursor: 'pointer' }}>Delete</button>
          )}
        </div>

        {/* Tabs */}
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1.5rem', marginBottom: '2rem' }}>
          <button className={activeTab === 'pdf' ? 'active' : ''} onClick={() => setActiveTab('pdf')} style={{ padding: '0.75rem 2rem', borderRadius: '8px', border: 'none', background: activeTab === 'pdf' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer', fontSize: '1.1rem' }}>📖 PDF Viewer</button>
          <button className={activeTab === 'quiz' ? 'active' : ''} onClick={() => setActiveTab('quiz')} style={{ padding: '0.75rem 2rem', borderRadius: '8px', border: 'none', background: activeTab === 'quiz' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer', fontSize: '1.1rem' }}>📝 Quiz</button>
          <button className={activeTab === 'chat' ? 'active' : ''} onClick={() => setActiveTab('chat')} style={{ padding: '0.75rem 2rem', borderRadius: '8px', border: 'none', background: activeTab === 'chat' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer', fontSize: '1.1rem' }}>💬 Chat</button>
          <button className={activeTab === 'progress' ? 'active' : ''} onClick={() => setActiveTab('progress')} style={{ padding: '0.75rem 2rem', borderRadius: '8px', border: 'none', background: activeTab === 'progress' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer', fontSize: '1.1rem' }}>📊 Progress</button>
          <button className={activeTab === 'youtube' ? 'active' : ''} onClick={() => setActiveTab('youtube')} style={{ padding: '0.75rem 2rem', borderRadius: '8px', border: 'none', background: activeTab === 'youtube' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer', fontSize: '1.1rem' }}>🎥 Videos</button>
        </div>

        {/* Main Content: Each tab full screen */}
        <div style={{ maxWidth: 1100, margin: '0 auto', background: 'white', borderRadius: '16px', boxShadow: '0 2px 16px rgba(0,0,0,0.07)', padding: '2.5rem 2rem', minHeight: '60vh' }}>
          {activeTab === 'pdf' && <PdfViewer pdfId={selectedPdfId} />}
          {activeTab === 'quiz' && <QuizGenerator pdfId={selectedPdfId} userId={userId} />}
          {activeTab === 'chat' && <ChatUI pdfId={selectedPdfId} userId={userId} />}
          {activeTab === 'progress' && <ProgressDashboard userId={userId} />}
          {activeTab === 'youtube' && <YouTubeRecommender pdfId={selectedPdfId} cachedVideos={videoCache[selectedPdfId]} onCacheVideos={(videos) => handleCacheVideos(selectedPdfId, videos)} onRefresh={() => handleRefreshVideos(selectedPdfId)} />}
        </div>

        {/* Delete Modal */}
        {showDeleteModal && pdfToDelete && (
          <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.25)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
            <div style={{ background: 'white', borderRadius: '12px', padding: '2rem 2.5rem', boxShadow: '0 2px 16px rgba(0,0,0,0.15)', minWidth: 320 }}>
              <h3>Delete PDF</h3>
              <p>Are you sure you want to delete <b>{pdfToDelete.originalname}</b>?</p>
              <div style={{ display: 'flex', gap: '1rem', marginTop: '2rem', justifyContent: 'flex-end' }}>
                <button onClick={() => setShowDeleteModal(false)} style={{ background: '#f5f7fa', border: 'none', borderRadius: '6px', padding: '0.5rem 1.2rem', fontWeight: 600, cursor: 'pointer' }}>Cancel</button>
                <button onClick={handleConfirmDelete} style={{ background: '#ff4d4f', color: 'white', border: 'none', borderRadius: '6px', padding: '0.5rem 1.2rem', fontWeight: 600, cursor: 'pointer' }}>Delete</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}

export default App;
