import React, { useState } from 'react';
import './App.css';
import Layout from './components/Layout';
import SourceSelector from './components/SourceSelector';
import PdfViewer from './components/PdfViewer';
import QuizGenerator from './components/QuizGenerator';
import ProgressDashboard from './components/ProgressDashboard';
import ChatUI from './components/ChatUI';
import YouTubeRecommender from './components/YouTubeRecommender';


function App() {
  const [selectedPdfId, setSelectedPdfId] = useState('');
  const [userId] = useState('user_' + Date.now()); // Simple user ID generation
  const [activeTab, setActiveTab] = useState('quiz');
  // Cache for YouTube videos per PDF
  const [videoCache, setVideoCache] = useState({});

  // Handler to update cached videos for a PDF
  const handleCacheVideos = (pdfId, videos) => {
    setVideoCache((prev) => ({ ...prev, [pdfId]: videos }));
  };

  // Handler to clear cached videos for a PDF (on refresh)
  const handleRefreshVideos = (pdfId) => {
    setVideoCache((prev) => {
      const updated = { ...prev };
      delete updated[pdfId];
      return updated;
    });
  };

  return (
    <Layout>
      <div className="app-container">
        <header className="app-header">
          <h1>📚 BeyondChats</h1>
          <p>Learn smarter with AI-powered learning</p>
        </header>

        <div className="main-content">
          <div className="sidebar">
            <SourceSelector 
              onSelectPdf={setSelectedPdfId}
              selectedPdfId={selectedPdfId}
            />
            <div className="tab-selector" style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem', marginTop: '2rem' }}>
              <button 
                className={activeTab === 'pdf' ? 'active' : ''}
                onClick={() => setActiveTab('pdf')}
                style={{ padding: '0.75rem', borderRadius: '8px', border: 'none', background: activeTab === 'pdf' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer' }}
              >
                📖 PDF Viewer
              </button>
              <button 
                className={activeTab === 'quiz' ? 'active' : ''}
                onClick={() => setActiveTab('quiz')}
                style={{ padding: '0.75rem', borderRadius: '8px', border: 'none', background: activeTab === 'quiz' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer' }}
              >
                📝 Quiz
              </button>
              <button 
                className={activeTab === 'chat' ? 'active' : ''}
                onClick={() => setActiveTab('chat')}
                style={{ padding: '0.75rem', borderRadius: '8px', border: 'none', background: activeTab === 'chat' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer' }}
              >
                💬 Chat
              </button>
              <button 
                className={activeTab === 'progress' ? 'active' : ''}
                onClick={() => setActiveTab('progress')}
                style={{ padding: '0.75rem', borderRadius: '8px', border: 'none', background: activeTab === 'progress' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer' }}
              >
                📊 Progress
              </button>
              <button 
                className={activeTab === 'youtube' ? 'active' : ''}
                onClick={() => setActiveTab('youtube')}
                style={{ padding: '0.75rem', borderRadius: '8px', border: 'none', background: activeTab === 'youtube' ? '#e6f7ff' : '#f5f7fa', fontWeight: 600, cursor: 'pointer' }}
              >
                🎥 Videos
              </button>
            </div>
          </div>

          <div className="content-area">
            {/* Only show PDF viewer in its own tab */}
            {activeTab === 'pdf' && (
              <div className="pdf-viewer-section">
                <PdfViewer pdfId={selectedPdfId} />
              </div>
            )}

            <div className="interaction-section">
              {activeTab === 'quiz' && (
                <QuizGenerator 
                  pdfId={selectedPdfId} 
                  userId={userId}
                />
              )}
              {activeTab === 'chat' && (
                <ChatUI 
                  pdfId={selectedPdfId}
                  userId={userId}
                />
              )}
              {activeTab === 'progress' && (
                <ProgressDashboard userId={userId} />
              )}
              {activeTab === 'youtube' && (
                <YouTubeRecommender 
                  pdfId={selectedPdfId}
                  cachedVideos={videoCache[selectedPdfId]}
                  onCacheVideos={(videos) => handleCacheVideos(selectedPdfId, videos)}
                  onRefresh={() => handleRefreshVideos(selectedPdfId)}
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default App;
